targets.expr:
round(
  100 *
    sum(
      rate(container_cpu_usage_seconds_total{pod=~"liquidator-holesky-jato-v2-1-.*"}[1m])
    ) by (pod, container_name, namespace, slave)
      /
    sum(
      kube_pod_container_resource_limits{pod=~"liquidator-holesky-jato-v2-1-.*", resource="cpu"}
    ) by (pod, container_name, namespace, slave)
)
